import React from "react";
import Product from "./Product"
// import ProductList from "./testArray"
import NavButtons from "./NavButtons"
import { useEffect, useState } from "react";
import axios from "axios";

function ProductListPage() {
  const [products, setProducts] = useState([]);
  const [isChecked, setIsChecked] = useState([]); // its for checkbox


  useEffect(() => {
    getProducts();
  }, []);

  const handleCheckBox = (event) => {

    const {value, checked} = event.target;
    // console.log(value);
    // console.log(checked);
    if (checked){
      setIsChecked([...isChecked, value]);
    }else{
      setIsChecked(isChecked.filter(e => e!==value))
    }

    // console.log(isChecked);
}

 function  deleteCheckBoxes(){
    // console.log(isChecked);
    var deletedProductSkus = {
      "skus":isChecked
    }
    // console.log(deletedProductSkus);
     axios.delete(`http://127.0.0.1:8000/Api/index`, {data:JSON.stringify(deletedProductSkus)} ).then(function(response){
      console.log(response.data);

      getProducts(); // get products from API again

  });

  }

  function getSpecialAttr(product){
   // this method get product object and returned special Attribute for Product
    if (product.type === "book")
      return "Weight: " + product.weight + " KG";
    else if (product.type === "dvd")
      return  "Size: " + product.size + " MB";
    else if (product.type === "furniture")
      return "Dimension: " + product.dimension
  }

  function getProducts() {
    axios.get('http://127.0.0.1:8000/Api/index').then(function(response) {
        // console.log(response.data);

        if (response.data.length === 0)
        {
          setProducts([]);
        }
        setProducts(response.data);
    });
  }

    return (
      <div>
        <NavButtons
          name="Product List"
          btnName1 = "ADD"
          btnName2 = "MASS DELETE"
          linkName = "/addProduct"
          linkName2 = "/"
          type="button"
          btn2_id = "delete-product-btn"
          delSelectedCheckBoxes={deleteCheckBoxes}
        />
        {
          products.map(product => {
            return  <Product 
            key={product.sku} productValue={product.sku} productChecked={product.isChecked} handleChange={handleCheckBox} sku={product.sku} 
              name={product.name} price={product.price + " $"} specificAttrbute = {getSpecialAttr(product)}></Product>
          })
        }

      </div>
    );
  }

  export default ProductListPage;
