import React from "react";
import { Link } from 'react-router-dom';
import {useNavigate} from 'react-router-dom';
function NavButtons(props){


    const navigate = useNavigate();
    // const hrStyle = {
    //     height:"2px",
    //     borderWidth:"0",
    //     color:"gray",
    //     backgroundColor:"gray"
    // }

    function sendForm(event){
        // console.log("buton çalıştı" + event.target.type);

        if (event.target.type === "submit"){ // "save" button has type of submit while "Add" button type is button
            props.triggerFunction(event);
        } else if ((event.target.type === "buton")){
            navigate("/addProduct");
        }

    }

    function massDeleteFunc(){
        props.delSelectedCheckBoxes()
    }
    return (
        <div className="row product-list-group">
         <div className="col-10"><span className="product-list-span">{props.name}</span></div>
         <div className="col-2 ">

         {/* <Link to={props.linkName}> */}
            <button form={props.formId} type={props.type} onClick={sendForm} className="btn btn-outline-dark">{props.btnName1}</button>
         {/* </Link> */}

         <Link to={props.linkName2}>
            <button id={props.btn2_id}  onClick={massDeleteFunc} type="button" className="btn btn-outline-dark ml-3">{props.btnName2}</button>
        </Link>
         </div>
         {/* id="delete-product-btn" */}
         {/* <hr style={hrStyle}/> */}
        </div>
    )
}


export default NavButtons;
