import React, { useState } from "react";
import AddProductInnerForm from "./AddProductInnerForm";


function AddProductForm (){

    const [switcherValue, setswitcherValue] = useState("Dvd");


    function setSelect(event) {
        var selectedValue = event.target.value;
        setswitcherValue(selectedValue);
        // console.log(switcherValue);
        // alert(switcherValue);
    } 
    return (

        <form id="product_form" className="ml-3">
             <div className="form-group row">
                <label  class="col-sm-1 col-form-label">SKU</label>
                <div class="col-sm-4">
                <input type="text" className="form-control" />
                </div>
             </div>

             <div className="form-group row">
                <label  class="col-sm-1 col-form-label">Name</label>
                <div class="col-sm-4">
                <input type="text" className="form-control" />
                </div>
            </div>

            <div className="form-group row">
                <label  class="col-sm-1 col-form-label">Price($)</label>
                <div class="col-sm-4">
                <input type="text" className="form-control" />
                </div>
            </div>

            <div className="form-group row">
                <label  class="col-sm-1">Type Swittcher</label>
                <div class="col-sm-4">
                    <select class="custom-select mr-sm-2" id="productType" onChange={setSelect}>
                        {/* <option selected>Choose...</option> */}
                        <option selected value="Dvd">Dvd</option>
                        <option value="Furniture">Furniture</option>
                        <option value="Book">Book</option>
                    </select>
                </div>

            </div>

            {/* <div className="form-group row"> */}
                {switcherValue === 'Dvd' && <AddProductInnerForm id ="DVD" typeName= "Size (MB)"
                            typeId = "size" 
                            description = "please provide size of DVD"
                         />}
                {switcherValue === 'Book' && <AddProductInnerForm id ="Book" typeName= "Weight (KG)"
                            typeId = "weight" 
                            description = "please provide weight of Book"
                         />}

                {switcherValue === 'Furniture' &&   <form id={props.id} className="w-50 p-3 ml-3 border border-dark">
                             <div className="form-group row">
                                <label  class="col-sm-3 col-form-label">{props.typeName}</label>
                            <div class="col-sm-8">
                                <input type="text" id = {props.typeId} className="form-control" />
                </div>
            </div>
            <p>{props.description}</p>
        </form>}
            {/* </div> */}
        </form>

    )

}

export default AddProductForm;
