import React from "react";

function AddProductInnerForm(props) {
  function handleChange(event) {
    // Here, we invoke the callback with the new value
    props.onChangeEvent(event);
  }

  return (
    <div className="w-50 p-3 ml-3 border border-dark">
      <div className="form-group row">
        <label className="col-sm-3 col-form-label">{props.typeName}</label>
        <div className="col-sm-8">
          <input
            type="text"
            id={props.typeId}
            name={props.inputName}
            onChange={handleChange}
            value={props.productValue}
            className="form-control"
          />
          <p style={{ color: "red" }}>{props.errorExplanation}</p>
        </div>
      </div>
      <p>{props.description}</p>
    </div>
  );
}

export default AddProductInnerForm;
