import React, { useState } from "react";
import NavButtons from "./NavButtons";
import AddProductForm from "./AddProductForm";
import axios from "axios";

function AddProductPage() {
  const [inputs, setInputs] = useState({});

  // const navigate = useNavigate();

   function handleSubmit(formInputs) {
    // console.log("formInputs name" + formInputs.name);
    // console.log("formInputs type" + formInputs.type);
    // console.log(typeof formInputs);
    console.log(formInputs);
    setInputs(formInputs); // lets assing formInputs that comes from form to inputs state

    console.log(formInputs);
console.log(typeof formInputs);
    console.log("product type" + inputs.name);
    var product = {};
    if (inputs.type === "book") {
      product["sku"] = inputs.sku;
      product["name"] = inputs.name;
      product["price"] = inputs.price;
      product["type"] = inputs.type;
      product["weight"] = inputs.weight;
    } else if (inputs.type === "dvd") {
      console.log("dvd içindeyiz");
      product["sku"] = inputs.sku;
      product["name"] = inputs.name;
      product["price"] = inputs.price;
      product["type"] = inputs.type;
      product["size"] = inputs.size;
    } else if (inputs.type === "furniture") {
      product["sku"] = inputs.sku;
      product["name"] = inputs.name;
      product["price"] = inputs.price;
      product["type"] = inputs.type;
      product["dimension"] =
        inputs.height + "x" + inputs.width + "x" + inputs.length;
    }

    // console.log(typeof product);
    // console.log(product);
    // console.log("addProdcutPage.jsx product : " + product);
    axios
      .post("http://127.0.0.1:8000/Api/index", JSON.stringify(product))
      .then(function (response) {
        // alert(response.data);
        console.log(response.data);
      });
  }

  // function submitForm(){
  //   // console.log("form gönderildi");
  //   // alert("hello world");
  //   // event.preventDefault();
  //   // navigate("/");

  // }
  return (
    <div>
      <NavButtons
        name="Product Add"
        btnName1="Save"
        btnName2="Cancel"
        // linkName = "/" // SILMELISIN
        linkName2="/"
        btn2_id="cancel-btn"
        type="submit"
        formId="product_form"
        // triggerFunction={handleSubmit}
        delSelectedCheckBoxes={() => {}} // create a dummy(empty) function
      />

      {/* submitForm = {submitForm} */}
      <AddProductForm sendInputs={handleSubmit} />
      {/* <AddProductForm sendInputs={formInputs=> {handleSubmit(formInputs) } }/> */}
      {/* <AddProductForm sendInputs={(inputs) => setInputs(inputs)} /> */}
    </div>
  );
}

export default AddProductPage;
