import React, { useState }  from "react";
import NavButtons from "./NavButtons"
import AddProductForm from "./AddProductForm"
import axios from "axios";
function AddProductPage(){

  const [inputs, setInputs] = useState({});

  function handleSubmit(){
    // console.log("handle submit function is triggered");
    // console.log(inputs.type);

    // event.preventDefault();

    var product = {} ;
    if (inputs.type === "book"){
      product["sku"] = inputs.sku;
      product["name"] = inputs.name;
      product["price"] = inputs.price;
      product["type"] = inputs.type;
      product["weight"] = inputs.weight;
    }
    else if (inputs.type === "dvd"){ 
      product["sku"] = inputs.sku;
      product["name"] = inputs.name;
      product["price"] = inputs.price;
      product["type"] = inputs.type;
      product["size"] = inputs.size;
    }
    else if (inputs.type === "furniture"){
      product["sku"] = inputs.sku;
      product["name"] = inputs.name;
      product["price"] = inputs.price;
      product["type"] = inputs.type;
      product["dimension"] = inputs.height + "x" + inputs.width + "x" + inputs.length;
    }


    // console.log(typeof product);
    // console.log(product.furniture);
    console.log(product);
    axios.post('http://127.0.0.1:8000/Api/index', JSON.stringify(product)).then(function(response){
        // alert(response.data);
        console.log(response.data);
    });

  }
    return (
        <div>       
         <NavButtons
                name="Product Add"
                btnName1 = "Save"
                btnName2 = "Cancel"
                linkName = "/"
                linkName2 = "/"
                btn2_id = "cancel-btn"
                type="submit"
                formId
                triggerFunction={handleSubmit}
        />
        <AddProductForm sendInputs ={inputs => setInputs(inputs)} />

      </div>
    )
}


export default AddProductPage;
