import React, { useState, useEffect} from "react";
import AddProductInnerForm from "./AddProductInnerForm";


function AddProductForm (props){

    const [switcherValue, setswitcherValue] = useState("dvd"); // this state created for switch operation
    const [inputs, setInputs] = useState({sku:"", name:"", price:"",type:"dvd", size:""});  // this state is created for submitting form
    const [formErrors, setFormErrors] = useState({}); // Validation error array
    const [isSubmit, setIsSubmit] = useState(false);



    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        // console.log("test name: " + name);
        setInputs(values => ({...values, [name]: value}))

        props.sendInputs(inputs);
    }


    function setSelect(event) { // this is for switch select operation
        var selectedValue = event.target.value;
        //  console.log(selectedValue);
        setswitcherValue(selectedValue);
        setInputs(values => ({...values,"type":selectedValue, "size":"", "weight":"", "height":"" 
                , "length":"", "width":""}))
    } 


    useEffect(() => {
        // console.log(formErrors);
        if (Object.keys(formErrors).length === 0 && isSubmit) {
          console.log(inputs);
        }
      }, [formErrors]);

    function submitFormInner(event){

        setFormErrors(validate(inputs));
        setIsSubmit(true);
        event.preventDefault();
        if (Object.keys(formErrors).length === 0 && isSubmit) // if there is now error and isSubmit flag true submit the form
            props.submitForm();
        // return 
        //     <div>
        //        {Object.keys(formErrors).length === 0 && isSubmit ? (
        //         <Link to={"/"}> </Link>) : (<p></p>)}
        //     </div>
     }

    useEffect(() => {
        // console.log(formErrors);
        if (Object.keys(formErrors).length === 0 && isSubmit) {
        console.log(inputs);
        }
    }, [formErrors]);


    const validate = (values) => {
        const errors = {};

        // var regex = new RegExp("/^(?!$)\d{0,10}(?:\.\d{1,2})?$/gm");
        const regex = /^(?!$)\d{0,10}(?:\.\d{1,2})?$/gm; 
                                                        // this match with decimal(10,2); 
                                                                // 1- Maximum 8 digits before decimal(.) point, 
                                                                // 2- Maximum 2 digits after decimal point(or its optional)



        const regex2 = /^(?!$)\d{0,11}$/gm; // this regex for size input


        if (!values.sku){
            errors.sku = "Please, submit required data";
        } 
        if (!values.name){
            errors.name = "Please, submit required data";
        } 
        if (!values.price){
            errors.price = "Please, submit required data";
        } else if((!regex.test(values.price))){
            errors.price = "Please, provide the data of indicated type"
        }

        if (values.type === "dvd" && !values.size){
            errors.size = "Please, submit required data";
        } else if (values.type === "dvd" && (!regex.test(values.size))){
            errors.size = "Please, provide the data of indicated type"
        }


        return errors;
    }
    return (
        <div>
        {/* onSubmit={submitFormInner} */}
        <form id="product_form" className="ml-3" onSubmit={submitFormInner} >
             <div className="form-group row">
                <label  className="col-sm-1 col-form-label">SKU</label>
                <div className="col-sm-4">
                <input id="sku" name="sku" type="text"  className="form-control" placeholder="sku" value={inputs.sku}        
                     onChange={handleChange} />

                <p style={{color: "red"}}>{formErrors.sku}</p>
                </div>

             </div>

             <div className="form-group row">
                <label  className="col-sm-1 col-form-label">Name</label>
                <div className="col-sm-4">
                <input id="name" name ="name" type="text" className="form-control" placeholder="name" value={inputs.name} 
                    onChange={handleChange} 
                />                
                <p style={{color: "red"}}>{formErrors.name}</p>
                </div>

            </div>



            <div className="form-group row">
                <label  className="col-sm-1 col-form-label">Price($)</label>
                <div className="col-sm-4">
                <input id="price" name="price" type="text" className="form-control" placeholder="price" value={inputs.price} 
                    onChange={handleChange} 
                />
                <p style={{color: "red"}}>{formErrors.price}</p>
                </div>
            </div>


            <div className="form-group row">
                <label  className="col-sm-1">Type Swittcher</label>
                <div className="col-sm-4">
                    <select className="custom-select mr-sm-2" id="productType" onChange={setSelect}>
                        {/* <option selected>Choose...</option> */}
                        <option id ="DVD" defaultValue="dvd" value="dvd">DVD</option>
                        <option id ="Book" value="furniture">Furniture</option>
                        <option id="Furniture"  value="book">Book</option>
                    </select>
                </div>

            </div>


                {switcherValue === 'dvd' && <AddProductInnerForm  inputName="size" onChange={handleChange} value={inputs.size} typeName= "Size (MB)"
                            typeId = "size" 
                            description = "please provide size of DVD"
                            errorExplanation={formErrors.size}
                         />}
                {switcherValue === 'book' && <AddProductInnerForm  inputName="weight" onChange={handleChange} value={inputs.weight} typeName= "Weight (KG)"
                            typeId = "weight" 
                            description = "please provide weight of Book"
                            errorExplanation={"formErrors.size"}
                         />}

                {switcherValue === 'furniture' &&   <div className="w-50 p-3 ml-3 border border-dark">
                             <div className="form-group row">
                                <label  className="col-sm-3 col-form-label">Height (CM)</label>
                                <div className="col-sm-8">
                                    <input type="text" id = "height" name="height" value={inputs.height} className="form-control" 
                                        onChange={handleChange}
                                    />
                                </div>

                                <label  className="col-sm-3 col-form-label">Width (CM)</label>
                                <div className="col-sm-8">
                                    <input type="text" id = "width" name="width" className="form-control" value={inputs.width}
                                        onChange={handleChange}  />
                                </div>

                                <label  className="col-sm-3 col-form-label">Length (CM)</label>
                                <div className="col-sm-8">
                                    <input type="text" id = "length" name="length" className="form-control" value={inputs.length}
                                        onChange={handleChange} />
                                </div>
                            </div>
                             <p>Please Provide dimensions in HxWxL Format</p>
                    </div>}
         </form>




        </div>
    )

}

export default AddProductForm;
