import React from "react";
import Product from "./Product"
// import ProductList from "./testArray"
import NavButtons from "./NavButtons"
import { useEffect, useState } from "react";
import axios from "axios";

function ProductListPage() {
  const [products, setProducts] = useState([]);
  const [isChecked, setIsChecked] = useState()
  useEffect(() => {
    getProducts();
  }, []);

  function getProducts() {
    axios.get('http://127.0.0.1:8000/Api/index').then(function(response) {
        // console.log(response.data);
        setProducts(response.data);
    });
  }


  function getSpecialAttr(product){
   // this method get product object and returned special Attribute for Product
    if (product.type === "book")
      return "Weight: " + product.weight + " KG";
    else if (product.type === "dvd")
      return  "Size: " + product.size + " MB";
    else if (product.type === "furniture")
      return "Dimension: " + product.dimension
  }
    return (
      <div>
        <NavButtons
          name="Product List"
          btnName1 = "ADD"
          btnName2 = "MASS DELETE"
          linkName = "/addProduct"
          linkName2 = "/"
          type="button"
          btn2_id = "delete-product-btn"
        />
        {
          products.map(product => {
            return  <Product 
            key={product.sku} productValue={product.sku} productChecked={product.isChecked} sku={product.sku} name={product.name} price={product.price + " $"} specificAttrbute = {getSpecialAttr(product)}></Product>
          })
        }

      </div>
    );
  }

  export default ProductListPage;
